<script>
  import data from './wheel'

  export default {
    name: "wheel",
    data() {
      return {
        data: data
      }
    },
    props: {
      items: '',
      theme: ''
    },
	  computed: {
      barriers(){
        let barriers = this.data.aspects[this.data.currentAspect].barriers;
        return barriers.join(', ');
      },
		  opinions(){
        let opinions = this.data.aspects[this.data.currentAspect].opinions;
        return opinions;
		  }
	  },
    methods: {
      changeAspect(aspect) {
        this.data.currentAspect = aspect;
      },
      init() {
        $('#wheel2 .piece').on('click', function (e) {
          $('#wheel2 .piece').removeClass('active');
          $(this).addClass('active');
        })
      }
    },
    mounted() {
      this.init();
    }
  }
</script>